package com.javaex.study01;

public class Ex15 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Practice03 Ex09
		//기존에 과제 한거랑 다른 방법으로 짰는데 더 간결해서 좋아보임
		for(int y=1; y<=10; y++) {
			for(int x=0; x<10; x++) {
				System.out.print(x+y+"\t");
			}System.out.println("");
		}
	}

}
